package kz.jibergroup.studyinn.presentation.profile

import android.ibanking.altyn.presentation.global.base.BaseViewModel
import androidx.lifecycle.MutableLiveData
import kz.jibergroup.studyinn.data.repo.PersonalRepository
import kz.jibergroup.studyinn.domain.entity.ProfileItem
import kz.jibergroup.studyinn.presentation.image_dialog.ImageItem

class ProfileViewModel(private val personalRepository: PersonalRepository) : BaseViewModel() {

    val userData = MutableLiveData<ProfileItem>()
    val userNotificationData = MutableLiveData<UserLocalNotificationItem>()
    val certificates = MutableLiveData<ArrayList<ImageItem>>()


    fun getCertificates(){

        val dataCertificates = arrayListOf<ImageItem>()
        dataCertificates.add(ImageItem(link = "https://www.theoryofchange.org/wp-content/uploads/2018/11/TOC-certificate.jpg"))
        dataCertificates.add(ImageItem(link = "https://www.apps4sailing.com/wp-content/uploads/2015/01/certificate.jpg"))
        dataCertificates.add(ImageItem(link ="https://cdn.shopify.com/s/files/1/0077/7604/3071/files/Intro_International_Trade_and_eBusiness-450x318_large.png?v=1552412309"))
        dataCertificates.add(ImageItem(link ="data:image/jpeg;base64,"))

        certificates.value = dataCertificates
    }

    fun getUserData() {
        userData.value = personalRepository.getUserInfo()
        getLocalNotificationData()
        getCertificates()
    }

    fun getLocalNotificationData(){
        userNotificationData.value = personalRepository.getNotificationData()
    }

    fun saveLocalNotificationData(notificationItem: UserLocalNotificationItem){
        personalRepository.saveNotificationData(notificationItem)
        userNotificationData.value = personalRepository.getNotificationData()
    }


}
